<?phpclass Commentaire {    //Attributes    private $id;    private $idImage;    private $texte;    private $auteur;    private $listeCommentairesFils;    //Getters    public function getId()    {        return $this->id;    }    public function getIdImage()    {        return $this->idImage;    }    public function getTexte()    {        return $this->texte;    }    public function getAuteur()    {        return $this->auteur;    }    public function getListeCommentairesFils()    {        return $this->listeCommentairesFils;    }}